pragma solidity ^0.5.4;


/**
 * @title Subscription manager base implementation interface.
 */
interface ISubscriptionManager {

    event SubscriptionCreated(bytes32 subscriptionHash);
    event SubscriptionCancelled(bytes32 subscriptionHash, bool expired);
    event SubscriptionExecuted(bytes32 subscriptionHash);

    enum PeriodUnit {
        Day,
        Month,
        Year
    }

    function isCancelled(bytes32 subscriptionHash)
        external
        view
        returns(bool);

    function isActive(bytes32 subscriptionHash)
        external
        view
        returns(bool);

    function nextPaymentTimestamp(bytes32 subscriptionHash) 
        external 
        view
        returns(uint256 timestamp);

    function getSubscription(bytes32 subscriptionHash)
        external
        view
        returns(
            address subscriber,
            address token,
            uint256 amount,
            PeriodUnit periodUnit,
            uint256 periods,
            uint256 maxPeriods,
            uint256 start
        );

    function execute(bytes32 subscriptionHash) external;

    function cancel(bytes32 subscriptionHash) external;
}
